import React from 'react'
import axios from 'axios';
import { toast } from 'react-toastify';
function Payment() {
    let promise = () => {
        const resolveAfter3Sec = new Promise(resolve => 
        setTimeout(resolve, 3000));
        // Start  
         //const data = {
             //cardhodername: data,
              //cardnumber : data,
             //month : data,
              //cvv: data
          //}
         //axios.post('http://localhost:8787/gateway/pay', data)
