import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router";
import { Link } from "react-router-dom";
import "../css/style.css";
// import PaymentHeader from "./PaymentHeader";
// import NavBarStudent from "./NavBarStudent";

function AddOrder() {
  const [order, setOrder] = useState({
    carModel: "",
    carName: "",
    contactno: "",
    date: "",
    orderId: "",
    username: "",
    washpackId: "",
    address: "",
  });
  const navigate = useNavigate();
  const [formErrors, setFormErrors] = useState({});

  const handleChange = (event) => {
    setOrder((order) => ({
      ...order,
      [event.target.name]: event.target.value,
    }));
  };
  const handleSubmit = () => {
    //validate add payment form
    let errors = {};

    if (!order.carModel) {
      errors["carModel"] = "carModel is required";
    }

    // if (typeof payment.cardNumber !== "undefined") {
    //   // this expression only for master card (/^(?:5[1-5][0-9]{14})$/)

    //   var pattern = new RegExp(/^[0-9]{16}$/);
    //   if (!pattern.test(payment.cardNumber)) {
    //     errors["cardNumber"] = "Please enter  valid 16 digit card number.";
    //   }
    // }

    if (!order.carName) {
      errors["carName"] = "carName is required";
    }

    if (!order.date) {
      errors["date"] = "date is required";
    }
    // if (typeof payment.description !== "undefined") {
    //   var pattern = new RegExp(/^[A-Za-z]+$/);
    //   if (!pattern.test(payment.description)) {
    //     errors["description"] = "Please type only character ";
    //   }
    // }

    if (!order.username) {
      errors["username"] = "username is required";
    }
    if (!order.address) {
      errors["address"] = "address is required";
    }
    if (!order.contactno) {
      errors["contactno"] = "contactno is required";
    }

    // if (typeof payment.course !== "undefined") {
    //   var pattern = new RegExp(/^[A-Za-z]+$/);
    //   if (!pattern.test(payment.course)) {
    //     errors["course"] =
    //       "Please type only character not number or special character";
    //   }
    // }

    setFormErrors(errors);
    const noErrors = Object.keys(errors).length === 0;
    if (noErrors) {
      alert("Order Submitted Successfully");
      const myOrder = JSON.parse(localStorage.getItem("myOrder"));
      const payload = {
    carModel: order.carModel,
    carName:order.carName,
    contactno: order.contactno,
    date: order.date,
    orderId: order.orderId,
    username: order.carName,
    washpackId: order.washpackId,
    address: order.address,
      };
      axios
        .post("http://localhost:8081/order/addorder", payload)
        .then((resp) =>
          alert(
            "order is saved with orderId:" + resp.data.orderId
          )
        );
      navigate(-1);
    }
  };
  return (
    <div className="form"  style={{marginTop:"7%"}}>
      
      <div  style={{fontSize:"20px"}}className="mrginlistalltrainer"> <a class="previous">&laquo;</a> 
      <Link to="/Userhome"  style={{color:"chocolate"}} className="previous round"><u>Back</u></Link> </div>
      {/* <NavBarStudent/> */}

      <div className="formTop">
        <div className="formTitle">
          <p className="c1">
            <h1>BOOK YOUR CAR WASH </h1>
          </p>
        </div>
         </div>
      <div>
         <div className="formDetails">
          <form className="actualForm" onSubmit={submitHandler}>
            <div style={{ marginBottom: "20px" }}>
              <p className="formName">FILL BELOW DETAILS</p>
            </div>
            
      
      <div className="form-body">
        {/* <PaymentHeader /> */}
        <div className="date">
          <label className="form__label" for="date">
            Date
          </label>
          <input
            className="form__input"
            id="Date"
            type="date"
            name="date"
            placeholder="Date"
            value={payment.Date}
            onChange={handleChange}
          />
          {formErrors.date && (
            <div style={{ color: "red", paddingBottom: 10 }}>
              {formErrors.date}
            </div>
          )}
        </div>
        <div className="carModel">
          <label
            for="carModel"
            onChange={handleChange}
            className="form__label"
          >
            CardModel
          </label>

          <select
            name="carModel"
            value={order.carModel}
            onChange={handleChange}
          >
            <option value="">select</option>
            <option value="toyota fortuner">Toyota Fortuner</option>
            <option value="swift ZXI plus">swift ZXI plus</option>
          </select>
          {formErrors.carModel && (
            <div style={{ color: "red", paddingBottom: 10 }}>
              {formErrors.carModel}
            </div>
          )}
        </div>
        <div>
          <label for="cars" onChange={handleChange} className="form__label">
          carName
          </label>

          <select
            name="carName"
            value={order.carName}
            onChange={handleChange}
          >
            <option value="">select</option>
            <option value="bmw">BMW</option>
            <option value="swift">Swift</option>
            <option value="toyota">Toyota</option>
            <option value="maruti">Maruti</option>
          </select>
          {formErrors.carName && (
            <div style={{ color: "red", paddingBottom: 10 }}>
              {formErrors.carName}
            </div>
          )}
        </div>
        <div className="contactno">
          <label className="form__label" for="contactno">
          ContactNo
          </label>
          <input
            className="form__input"
            type="number"
            id="contactno"
            name="contactno"
            placeholder="contactno"
            value={order.contactno}
            onChange={handleChange}
          />
          {formErrors.contactno && (
            <div style={{ color: "red", paddingBottom: 10 }}>
              {formErrors.contactno}
            </div>
          )}
        </div>
        <div className="username">
          <label className="form__label" for="username">
          UserName
          </label>
          <input
            className="form__input"
            type="text"
            id="username"
            name="username"
            placeholder="username"
            value={order.username}
            onChange={handleChange}
          />
          {formErrors.username && (
            <div style={{ color: "red", paddingBottom: 10 }}>
              {formErrors.username}
            </div>
          )}
        </div>
        <div className="address">
          <label className="form__label" for="address">
          Address
          </label>
          <input
            className="form__input"
            type="text"
            id="address"
            name="address"
            placeholder="address"
            value={order.address}
            onChange={handleChange}
          />
          {formErrors.address && (
            <div style={{ color: "red", paddingBottom: 10 }}>
              {formErrors.address}
            </div>
          )}
        </div>

        {/* <div className="paymentDate">
          <label className="form__label" for="paymentDate">
            PaymentDate
          </label>
          <input
            className="form__input"
            type="date"
            id="paymentDate"
            name="paymentDate"
            placeholder="Payment Date"
            value={payment.paymentDate}
            onChange={handleChange}
          />
          {formErrors.paymentDate && (
            <div style={{ color: "red", paddingBottom: 10 }}>
              {formErrors.paymentDate}
            </div>
          )} */}
        {/* </div> */}
      </div>
      {/* <div orderId="outer">
        <div class="inner1">
          <button className="btn btn-primary mb-2" onClick={handleSubmit}>
            Save
          </button>
        </div>
        <div class="inner2">
          <Link to="/Userhome" className="btn btn-danger">
            {" "}
            Cancel{" "}
          </Link>
        </div> */}
        </form>
        </div>
       </div>
      </div>
    
  );
}
export default AddOrder;
