import React,{useEffect, useState} from 'react';
import axios from 'axios';
import { useParams } from 'react-router';
import { useNavigate } from "react-router";
import { Link } from 'react-router-dom';
function  DeleteOrder() {
    const[order, setOrder] = useState(null)
    const { id } = useParams();
    const navigate=useNavigate();
    const fetchOrderById = () => {
        axios.get("http://localhost:8081/order/viewOrder/"+ id).
        then(resp => setOrder(resp.data))
    }
    useEffect(fetchOrderById, []);
    const DeleteOrder = () => {
        axios.delete( "http://localhost:8081/order/delete/"+id).
        then(resp => alert("order id:"+resp.data.OrderId +" is deleted successfully........"));
        navigate(-1);
    }
    return (
        <div  style={{marginTop:"7%"}}>

            <div  style={{fontSize:"20px"}}className="mrginlistalltrainer"> <a class="previous">&laquo;</a> 
      <Link to="/"  style={{color:"chocolate"}} className="previous round"><u>Back</u></Link> </div>
            <h2  style={{color:"green"}}><u>Order Details</u></h2>
            {
                order !== null &&
                <div>
                    <p>OrderId: {order.orderId}</p>
                    <p> Car Name: {order.carName}</p>
                    <p> Car Model: {order.carModel}</p>
                    <p> Contactno: {order.contactno}</p>
                    <p> Date: {order.date}</p>
                    <p> Address: {order.address}</p>
                    <p> Username: {order.username}</p>
                </div>
            }
            <div><button className = "btn btn-primary mb-2" onClick={DeleteOrder}>Delete</button></div>

        </div>
    )
}
export default DeleteOrder;
