import React, { Component } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import { Table } from "react-bootstrap";
import Footer from "./Footer";
import { Link } from "react-router-dom";

export default class WasherProfile extends Component {
  // Start
  // Constructor
  constructor(props) {
    super(props);

    this.state = {
      items: [],
      DataisLoaded: false,
    };
  }

  // ComponentDidMount is used to
  // execute the code
  componentDidMount() {
    fetch("http://localhost:8082/washer/allwasher")
      .then((res) => res.json())
      .then((json) => {
        this.setState({
          items: json,
          DataisLoaded: true,
        });
      });
  }
  //End

  render() {
    const { DataisLoaded, items } = this.state;

    return (
      <>
        <div  style={{marginTop:"7%"}}>
        <div  style={{fontSize:"30px"}} className="mrginlistalltrainer"> <a class="previous">&laquo;</a> 
      <Link to="/getAllMaterial"  style={{color:"chocolate"}}className="previous round">Back</Link> </div>
      <h2 style={{color:"green"}}><u>Material By Id</u></h2>
          <Table striped bordered hover variant="dark">
            <thead>
              <tr>
                <th>Id</th>
                <th>Name</th>
                <th>Location</th>
              </tr>
            </thead>
          </Table>
        </div>
        {items.map((item) => (
          <div>
            <Table striped bordered hover variant="dark">
              <tbody>
                <tr>
                  <td>{item.id}</td>
                  <td>{item.name}</td>
                  <td>{item.location}</td>
                </tr>
              </tbody>
            </Table>
          </div>
        ))}

        <div>
          <Footer />
        </div>
      </>
    );
  }
}
