import React, { useState } from "react";
import axios from "axios";
import { baseUrl } from "../util/AppConstants";
// import "../css/AddTrainer.css"

import { useNavigate } from "react-router";
import { Link } from "react-router-dom";

function Addpack() {
  const [pack, setPack] = useState({
   cost: 0,
  description:"",
  id: 0,
  packname:""
  });
  const navigate = useNavigate();
  const [formErrors, setFormErrors] = useState({});
  const handleChange = (event) => {
    setPack((pack) => ({
    ...pack,
      [event.target.name]: event.target.value,
      //[event.target.name]: event.target.type === 'String' ? parseInt(event.target.value) : event.target.value,
    }));
  };
  const handleSubmit = () => {
    //validate add trainer form
    let errors = {};
    
    if (!pack.packname) {
      errors["packnameErr"] = "packname is required";
    }
    
    if (!pack.cost) {
      errors["costErr"] = "cost is required";
    }
    if (!pack.description){
        errors["descriptionErr"]="description is required";
    }
    setFormErrors(errors);
    const noErrors = Object.keys(errors).length === 0;
    if (noErrors) {
      const payload = {
          
        cost: pack.cost,
        packname: pack.packname,
        description:pack.description,
      };
      axios
        .post(
            "http://localhost:8061/admin/addwashpack",
          payload
        )
        .then((resp) =>
          alert("pack is saved with id:" + resp.data.id)
        );
        navigate(-1);
    }
  };
  return (
    <div  className="trainerbgpg" style={{marginTop:"6%"}}>
      <NavBarAdmin/>
      <div  style={{fontSize:"20px"}}className="mrginlistalltrainer"> <a class="previous">&laquo;</a> 
      <Link to="/admin_home"  style={{color:"chocolate"}} className="previous round"><u>Back</u></Link> </div>
     <div>
        <label>pack Name</label>
        <input
        id="pname"
         className="form-control"
          type="text"
          name="packname"
          value={pack.packname}
          onChange={handleChange}
        />
         {formErrors.packnameErr && (
          <div style={{ color: "red", paddingBottom: 10 }}>
            {formErrors.packnameErr}
          </div>
        )}
      </div>
      
      <div>
        <label>cost</label>
        <input
        id="cost"
         className="form-control"
          type="numbar"
          name="cost"
          value={pack.cost}
          onChange={handleChange}
        />
         {formErrors.costErr && (
          <div style={{ color: "red", paddingBottom: 10 }}>
            {formErrors.costErr}
          </div>
        )}
      </div>
      <div>
        <label>Descripton</label>
        <input
        id="description"
         className="form-control"
          type="text"
          name="description"
          value={pack.description}
          onChange={handleChange}
        />
         {formErrors.descriptionErr && (
          <div style={{ color: "red", paddingBottom: 10 }}>
            {formErrors.descriptionErr}
          </div>
        )}
      </div>

      <div id="outer" >
        <div  class="inner1"><button className = "btn btn-primary mb-2"  onClick={handleSubmit}>Save</button></div>
        {/* <div class="inner2"><Link to="/getAllCourse" className="btn btn-danger"> Cancel </Link></div> */}
      </div>
    </div>
  );
}
export default Addpack;
