import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate, useParams, } from 'react-router';
import { Link } from 'react-router-dom';
// import { baseUrl } from '../util/AppConstants';
// import NavBarAdmin from './NavBarAdmin';
function UpdatePack() {
    const [pack, setPack] = useState({
      cost: "",
        packname: "",
         description: ""
         

    });
    const { id } = useParams();

    const navigate = useNavigate();
    const fetchPackById = () => {
        axios.get("http://localhost:8061/admin/viewPack/"+id)
        .then(resp => setPack(resp.data))
    }
    useEffect(fetchPackById, []);
    const handleChange = (event) => {
        event.persist();
        setPack(pack => ({ ...pack, [event.target.name]: event.target.value }));
    }
    const handleSubmit = () => {
        const palyload = {
            id: pack.id,
            packname: pack.packname,
            description:pack.description,
            cost:pack.cost
        }
        axios.put("http://localhost:8061/admin/washpack/update", palyload)
            .then(resp => alert(" id: " +resp.data.id +" updated successfully........"));
        navigate(-1);
    }
    return (
        <div  style={{marginTop:"8%"}}>
            <div className="container-fluid " style={{width:"400px"}} ></div>
            <div >
                    <h3><strong>Update Washpack</strong></h3>
            </div>

            {/* <div  style={{fontSize:"20px"}}className="mrginlistalltrainer"> <a class="previous">&laquo;</a>
      <Link to="/admin_home"  style={{color:"chocolate"}} className="previous round"><u>Back</u></Link> </div> */}
             <h2 style={{color:"green"}}><u>Details</u></h2>
            <div>
                <label style={{  color: "black" }}> ID:</label>
                <input type="text" className="form-control" name="id" value={pack.id} onChange={handleChange} style={{ fontWeight: "700" }} disabled />
                {
                                            formErrors.IdErr && <div style={{ color: "red", paddingBottom: 10 }}>{formErrors.IdErr}</div>
                                        }
            </div>
            <div>
                <label>PackName:</label>
                <input type="text" name="packname" value={pack.packname} onChange={handleChange} />
            </div>

            <div>
                <label>Description:</label>
                <input type="text" name="cdescription" value={pack.description} onChange={handleChange} />
            </div>
            <div>
                <label>Cost:</label>
                <input type="text" name="cost" value={pack.cost} onChange={handleChange} />
            </div>

            <div>
                <button className = "btn btn-primary mb-2" onClick={handleSubmit}>Update</button>
            </div>
        </div>
    )
}
export default  UpdatePack
