import React, { useEffect, useState } from "react";
import axios from "axios";
import { Link } from "react-router-dom";
//import { baseUrl } from "../util/AppConstants";
// import "../css/ViewAllMessages.css";
import AdminNab from "./AdminNab";

export default function Profile() {
  const [messages, setMessages] = useState([]);

  const fetchAll = () => {
    axios
      .get("http://localhost:8083/user/allusers")
      .then((resp) => setMessages(resp.data));
  };
  useEffect(fetchAll, []);

  return (
  
    <div className="container-fluid">
         <AdminNab/>
      <div className="mrginprvmsg">
        {/* <Link
          to="/admin_home"
          style={{ color: "chocolate", fontSize: "30px" }}
          className="previous round"
        >
          Back
        </Link> */}
      </div>
      <h4
        style={{
          color: "red",
          backgroundColor: "MediumTurquoise",
          fontSize: "30px",
        }}
        className="text-center"
      >
        USER LIST
      </h4>

      {/* <Link to="/add/message" className="btn btn-primary mb-2">
   
        Add Message
      </Link> */}
      <table class="table table-bordered">
        <thead class="thead-dark">
          <tr>
                <th>Name</th>
                 <th>Username</th>
                <th>Email</th>
                <th>Contact</th>
            {/* <th colSpan={2}>Actions</th> */}
          </tr>
        </thead>

        <tbody>
          {messages.map((u, index) => (
            <tr key={index + 1}>
              {/* <td>{index+1}</td> */}
              <td>{u.name}</td>
                  <td>{u.username}</td>
                  <td>{u.email}</td>
                   <td>{u.contactno}</td>
              {/* <td>
                <Link
                  to={`/view/message/${m.messageId}`}
                  className="btn btn-primary mb-2"
                >
                  View
                </Link>
              </td> */}
              {/* <td>
                <Link
                  to={`/update/message/${m.messageId}`}
                  className="btn btn-primary mb-2"
                >
                  Update
                </Link>
              </td> */}
              {/* <td>
                <Link
                  to={`/message/delete/${m.messageId}`}
                  className="btn btn-primary mb-2"
                >
                  Delete
                </Link>
              </td> */}
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
