import React, { useEffect, useState } from "react";
import axios from "axios";
import { Link } from "react-router-dom";
//import { baseUrl } from "../util/AppConstants";
// import "../css/ViewAllMessages.css";
import AdminNab from "./AdminNab";

export default function WasherProfile() {
  const [washer, setWasher] = useState([]);

  const fetchAll = () => {
    axios
      .get("http://localhost:8082/washer/allwasher")
      .then((resp) => setWasher(resp.data));
  };
  useEffect(fetchAll, []);

  return (
    <div className="container-fluid " >
        <AdminNab/>
      <div className="mrginprvmsg " >

        <a style={{ fontSize: "30px" }} class="previous">
          &laquo;
        </a>
        {/* <Link
          to="/admin_home"
          style={{ color: "chocolate", fontSize: "30px" }}
          className="previous round"
        >
          Back
        </Link> */}
      </div>
      <h4
        style={{
          color: "red",
          backgroundColor: "powderblue",
          fontSize: "30px",
        }}
        className="text-center  col-md-5"
      >
        WASHER LIST
      </h4>
      <hr />

      {/* <Link to="/add/message" className="btn btn-primary mb-2">

        Add Message
      </Link> */}
      <table class="table table-bordered">
        <thead class="black white-text" style={{backgroundColor:"blue"}}>
          <tr>
          <th>Id</th>
           <th>Name</th>
            <th>Location</th>
            {/* <th colSpan={2}>Actions</th> */}
          </tr>
        </thead>

        <tbody>
          {washer.map((w, index) => (
            <tr key={index + 1}>
              {/* <td>{index+1}</td> */}
              <td >{w.id}</td>
              <td>{w.name}</td>
              <td>{w.location}</td>
              {/* <td>
                <Link
                  to={`/view/message/${m.messageId}`}
                  className="btn btn-primary mb-2"
                >
                  View
                </Link>
              </td> */}
              {/* <td>
                <Link
                  to={`/update/message/${m.messageId}`}
                  className="btn btn-primary mb-2"
                >
                  Update
                </Link>
              </td> */}
              {/* <td>
                <Link
                  to={`/message/delete/${m.messageId}`}
                  className="btn btn-primary mb-2"
                >
                  Delete
                </Link>
              </td> */}
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
