import React from 'react'
import { Link } from 'react-router-dom';
// import "../login.css";
import axios from 'axios';
// import { useNavigate } from 'react-router-dom'
import { useState } from 'react';
// import Navbar from './Navbar';


const UserLogin = () => {




  var auth = JSON.stringify(localStorage.getItem("auth"))
  console.log("this is auth string :" + auth);

  // const nav = useNavigate();

  const [formErrors, setFormErrors] = useState({});

  const [input, setInput] = useState({
    username: "",
    password: ""
  });

  const inputEvent = (event) => {
    const { name, value } = event.target;

    setInput((previousvalue) => {
      console.log(previousvalue.data);
      return {
        ...previousvalue,
        [name]: value,
      }
    });
  };
  const showdata = (event) => {
    event.preventDefault();
    let errors = {};

    if (!input.username) {
      errors["usernameErr"] = "username is required";
    }
    if (!input.password) {
      errors["passwordErr"] = "password is required";
    }

    setFormErrors(errors);
    const noErrors = Object.keys(errors).length === 0;
    const handleClick = () => {
    if (noErrors) {
      const payload = {
        username: input.username,
        password: input.password
      };

      try {
        axios.post("http://localhost:8083/user/authenticate", payload)
          .then((resp) =>
            alert("Welcome" + resp.data.username)
          );

      }
      catch (error) {
        console.log("error is", error)
      };
    }
  }
  }
  return (
    <>
      {/* <Navbar /> */}

      <div className="register" >
        <div className="form"
        // style={{
        //   backgroundImage: `url("https://images.unsplash.com/photo-1607082352121-fa243f3dde32?ixlib=rb-1.2.1&ixid=MnwxMjA3fDB8MHxzZWFyY2h8OHx8ZGlzY291bnR8ZW58MHx8MHx8&auto=format&fit=crop&w=600&q=60")`
        // }}
        >
          <form className="register-form">
            <h1>Login</h1>

            {/* <input type="text" name="username" placeholder="Enter username" onChange={inputEvent} value={input.username} required /> */}

            <div className="username">
              <label className="form__label" htmlFor="username">Username </label>
              <input type="text" id="username" className="form__input" name="username" placeholder="Enter username"
                value={input.username}
                onChange={inputEvent}
              />
              {formErrors.usernameErr && (
                <div style={{ color: "red", paddingBottom: 10 }}>
                  {formErrors.usernameErr}
                </div>
              )}
            </div>

            {/* <input type="password" name="password" placeholder="Enter password" onChange={inputEvent} value={input.password} required /> */}

            <div className="password">
              <label className="form__label" htmlFor="password">Password </label>
              <input className="form__input" type="password" id="password" name="password" placeholder="Enter Password"
                value={input.password}
                onChange={inputEvent}
              />
              {formErrors.passwordErr && (
                <div style={{ color: "red", paddingBottom: 10 }}>
                  {formErrors.passwordErr}
                </div>
              )}
            </div>

            <div className="footer">
              <button type="submit" className="btn" onClick={showdata}>Login</button>
              <Link to="/signup">

                Already User? Login here.


              </Link>

            </div>
            <button className="Btn" onClick={handleClick}>
            Submit
          </button>
          {/* <div>
          <MyFacebookLoginButton />
        </div>
        <div>
          <GoogleLoginButton />
        </div> */}
          <button
            className="Btn"
            onClick={() => {
              navigate("/Signup");
            }}
          >
            SignUp
          </button>

            {/* <button onClick={showdata}>Login</button>

            <div class="text-center">
              <Link to="/signup">

                New User? Register


              </Link>
            </div> */}
          </form>
        </div>
      </div>



    </>
  )
};
export default UserLogin;
