import React, { Component } from "react";
import { Link } from "react-router-dom";
import "./login.css";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
toast.configure();

/**To Show Add Toastify Text */
const notify = () => {
  toast.success("Registered Successfully!!", {
    position: "top-center",
    autoClose: 3000,
  });
};

export default class UserRegister extends Component {
  constructor() {
    super();
    this.state = {
      id:"",
      name: "",
      location: "",
      password: "",
      nameError: "",
      locationError: "",
      passwordError: "",
    };
    this.register = this.register.bind(this);
    this.handleChange = this.handleChange.bind(this);
  }
  valid() {
    if (
      this.state.id.length < 4 &&
      this.state.name.length < 4 &&
      this.state.location.length < 6 &&
      this.state.password.length < 6
    ) {
      this.setState({
        idError:"invalid Id",
        nameError: "Invalid Name",
        locationError: "Please Enter a currect location Id",
        passwordError: "Password length should be more than 6",
      });
    } else if (this.state.id.length < 1) {
      this.setState({
        nameError: "Id length should be more than 1",
      });
    } else if (this.state.name.length < 1) {
      this.setState({
        nameError: "Name length should be more than 1",
      });
    } else if (this.state.location.length < 9) {
      this.setState({
        emailError: "location length should be more than 8",
      });
    } else if (this.state.password.length < 6) {
      this.setState({
        passwordError: "Password length should be more than 6",
      });
    } else {
      return true;
    }
  }

  register(e) {
    this.setState({
      idError:"",
      nameError: "",
      locationError: "",
      passwordError: "",
    });
    e.preventDefault();
    if (this.valid()) {
      fetch("http://localhost:8082/washer/addwasher", {
        method: "POST",
        headers: {
          "content-type": "application/json",
          accept: "application/json",
          "Access-Control-Allow-Origin": "*",
        },
        body: JSON.stringify({
          id:this.state.id,
          name: this.state.name,
          location: this.state.location,
          password: this.state.password,
        }),
      })
        .then((response) => response.json())
        .then((response) => {
          toast.success("User Added Successfully!!", {
            position: "top-center",
            autoClose: 3000,
          });
        })
        .catch((err) => {
          notify();
        });
    }
  }

  handleChange(changeObject) {
    this.setState(changeObject);
  }

  render() {
    return (
      <form>
        
        <div
           className="form"
          // style={{ backgroundColor: "Plum", paddingLeft: "10px" }}
        >
          <div className="was">
          <h2 className="user-panel">WASHER REGISTRATION</h2>
          <div className="userregisterform">
            <label>
              <b>Id</b>
            </label>
            <input
              style={{ backgroundColor: "white" }}
              type="number"
              placeholder="Enter id"
              onChange={(e) => this.handleChange({ id: e.target.value })}
            />
            <p style={{ color: "red" }}>{this.state.idError}</p>
          </div>
          <div className="userregisterform">
            <label>
              <b>Name</b>
            </label>
            <input
              style={{ backgroundColor: "white" }}
              type="text"
              placeholder="Enter name"
              onChange={(e) => this.handleChange({ name: e.target.value })}
            />
            <p style={{ color: "red" }}>{this.state.nameError}</p>
          </div>

          <div className="userregisterform">
            <label>
              <b>location</b>
            </label>
            <input
              style={{ backgroundColor: "white" }}
              type="text"
              placeholder="Enter location"
              onChange={(e) => this.handleChange({ location: e.target.value })}
            />
            <p style={{ color: "red" }}>{this.state.locationError}</p>
          </div>

          <div className="userregisterform">
            <label>
              <b>Password</b>
            </label>
            <input
              style={{ backgroundColor: "white" }}
              type="password"
              placeholder="Enter password"
              onChange={(e) => this.handleChange({ password: e.target.value })}
            />
            <p style={{ color: "red" }}>{this.state.passwordError}</p>
          </div>
          <p style={{ color: "red" }}>{this.state.confirmpasswordError}</p>
        </div>
        </div>
        <div className="userregisterform">
          <Link
            type="submit"
            className="userregisterbtn"
            to="/search"
            onClick={(e) => {
              this.register(e);
            }}
          >
            Submit
          </Link>
          <ToastContainer />
        </div>
        <br></br>
        {/* <p className="a">
          Login as Member..? <a href="/WasherLog"> WasherLogin</a>
        </p> */}
      </form>
    );
  }
}
