import { toBeEmpty } from "@testing-library/jest-dom";
import axios from "axios";
import React from "react";
import { useState, useEffect } from "react";
import { toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import "./booking.css";
import { useNavigate } from "react-router-dom";
import Footer from "./Footer";
import "../pages/style.css";

toast.configure();
const Booking = () => {
  // Start
  const [washers, setWashers] = useState([]);

  useEffect(() => {
    fetch("http://localhost:8061/admin/washpack").then((result) => {
      result.json().then((resp) => {
        setWashers(resp);
        console.log(resp);
      });
    });
  }, []);

  // End
  let navigate = useNavigate();

  const [order, setOrder] = useState({
    carModel: "",
    carName: "",
    contactno: "",
    date: "",
    orderId: "",
    username: "",
    washpackId: "",
    address: "",
  });

  const changeHandler = (e) => {
    setOrder({ ...order, [e.target.name]: e.target.value });
  };

  const submitHandler = () => {
    axios
      .post("http://localhost:8081/order/addorder", order)
      .then((res) => {
        if (res.status === 200) alert("Booked Successfully");
        else Promise.reject();
      })
      .catch((err) => alert("Something went wrong"));
  };

  return (
    <>
      <div className="formTitle">
        <p className="c1">
          <div>
            <h1>BOOK YOUR CAR WASH </h1>
          </div>
        </p>
      </div>
      <div>
        <div
          className="formDetails"
          style={{ backgroundColor: "Plum", paddingLeft: "5px" }}
        >
          <form
            className="actualForm"
            onSubmit={submitHandler}
            style={{ paddingLeft: "10px" }}
          >
            <div style={{ marginBottom: "20px" }}>
              <p className="formName">FILL BELOW DETAILS</p>
            </div>
            <div>
              <p>Date of car wash</p>
              <input type="date" name="date" onChange={changeHandler} />
            </div>

            {/* {washers.map((emp, ind) => (
              <div style={{ marginBottom: "20px" }}>
                <option>{emp.packname}</option>
                <input type="radio" onChange={changeHandler} /> {emp.cost}
              </div>
            ))} */}

            <div style={{ marginBottom: "30px" }}>
              <p className="formName">CONTACT DETAILS</p>
            </div>
            <div>
              <input
                style={{ backgroundColor: "white" }}
                className="contactDetails"
                type="text"
                name="username"
                onChange={changeHandler}
                placeholder="Enter Your Full Name"
              />
              {/* <br />
              <input
                style={{ backgroundColor: "white" }}
                className="contactDetails"
                name="carName"
                type="text"
                placeholder="Enter Your Car Name"
                onChange={changeHandler}
              /> */}
              <div>
                <label for="carName" onChange={changeHandler}>
                  carName
                </label>
                <select
                  name="carName"
                  value={order.carName}
                  onChange={changeHandler}
                >
                  <option value="">select</option>
                  <option value="Swift">Swift</option>
                  <option value="BMW">BMW</option>
                  <option value="Toyota">Toyota</option>
                  <option value="Maruti Suzuki">Maruti Suzuki</option>
                  <option value="Ford">Ford</option>
                </select>
              </div>

              <div>
                <label for="carModel" onChange={changeHandler}>
                  CarModel
                </label>
                <select
                  name="carModel"
                  value={order.carModel}
                  onChange={changeHandler}
                >
                  <option value="">select</option>
                  <option value="ZXI Plus">ZXI Plus</option>
                  <option value="BMW X5">BMW X5</option>
                  <option value="Toyota Fortuner">Toyota Fortuner</option>
                  <option value="BMW 3 Series">BMW 3 Series</option>
                  <option value="Toyota Camry">Toyota Camry</option>
                </select>
              </div>
              <br />
              <input
                style={{ backgroundColor: "white" }}
                type="text"
                className="contactDetails"
                name="contactno"
                onChange={changeHandler}
                placeholder="Enter Phone"
              />
              <br />

              <textarea
                cols="30"
                className="contactDetailsTextArea"
                rows="10"
                type="text"
                name="address"
                onChange={changeHandler}
                placeholder="Your Address"
              />
            </div>
            <div className="Submit">
              <button
                type="submit"
                className="btn btn-success"
                onClick={() => {
                  navigate("/Payment");

                  submitHandler();
                }}
              >
                Submit
              </button>
            </div>
          </form>
        </div>
      </div>
      <div>
        <Footer />
      </div>
    </>
  );
};
export default Booking;
